document.getElementById("submit").addEventListener("click",e=>{e.preventDefault();let s=document.getElementById("email").value;const r=document.getElementById("icon"),t=document.getElementById("err_message"),a=document.getElementById("email");/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(s)?(a.classList.remove("error-border"),r.classList.remove("error-icon"),t.innerHTML="Great! Your email successfully sent.",t.classList.contains("error-msg")&&t.classList.remove("error-msg"),t.classList.add("ok")):(a.classList.add("error-border"),r.classList.add("error-icon"),t.innerHTML="Please provide a valid email",t.classList.contains("ok")&&t.classList.remove("ok"),t.classList.add("error-msg"))});
