document.getElementById("submit").addEventListener("click",()=>{let e=document.getElementById("email").value;const t=document.getElementById("icon"),s=document.getElementById("err_message"),r=document.getElementById("email");/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e)?(r.classList.remove("error"),t.classList.remove("error-icon"),s.classList.remove("err")):(r.classList.add("error"),t.classList.add("error-icon"),s.classList.add("err"),console.log(e))});
